import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "hardhat/console.sol";

contract RewardPool is Ownable {
    using ECDSA for bytes32;
    using SafeERC20 for ERC20;
    using SafeMath for uint256;

    address public rewardAddress;
    address public sourceAddress;
    // Mitigating Replay Attacks
    mapping(address => mapping(uint256 => bool)) seenNonces;


    // Event
    event ClaimReward(address toAddress, uint256 amount);

    constructor(address _rewardAddress, address _sourceAddress) {
      rewardAddress = _rewardAddress;
      sourceAddress = _sourceAddress;
    }

    modifier verifySignature(
        uint256 nonce,
        uint256 _amount,
        bytes memory signature
    ) {
        // This recreates the message hash that was signed on the client.
        bytes32 hash = keccak256(
            abi.encodePacked(msg.sender, _amount, nonce)
        );
        bytes32 messageHash = hash.toEthSignedMessageHash();
        // Verify that the message's signer is the owner of the order
        require(messageHash.recover(signature) == owner(), "Invalid signature");
        require(!seenNonces[msg.sender][nonce], "Used nonce");
        seenNonces[msg.sender][nonce] = true;
        _;
    }

    // Claim reward token
    // @param receiveAddress: address receive reward token
    // amount
    function claimReward(        
        uint256 amount,
        uint256 nonce,
        bytes memory signature
    ) external verifySignature(nonce, amount, signature) {
        // check allowance of source address
        require(
            IERC20(rewardAddress).allowance(sourceAddress, address(this)) >=
                amount,
            "token allowance of source address too low"
        );

        // transfer token from source address to receive address
        ERC20(rewardAddress).safeTransferFrom(
            sourceAddress,
            msg.sender,
            amount
        );

        emit ClaimReward(msg.sender, amount);
    }
}
